    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        // TODO add your handling code here:
      String selectedClass = (String) jComboBox1.getSelectedItem();
    String name = jTextField1.getText();
    String gender = (String) jComboBox2.getSelectedItem();

    RPGmodel character = RPGmodel.createCharacter(selectedClass, name, gender);

    if (character != null) {
        jTextArea1.setText(
            "Name: " + character.getName() + "\n" +
            "Gender: " + character.getGender() + "\n" +
            "Health: " + character.getHealth() + "\n" +
            "Attack Power: " + character.getAttackPower() + "\n" +
            "Defence: " + character.getDefence()
        );
    }
    }//GEN-LAST:event_jComboBox1ActionPerformed

