    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        // Create the character using the selected values
    String selectedClass = (String) jComboBox1.getSelectedItem();
    String name = jTextField1.getText();
    String gender = (String) jComboBox2.getSelectedItem();

    RPGmodel character = RPGmodel.createCharacter(selectedClass, name, gender);

    if (character != null) {
        // Create the GamePlayGUI and pass the created character
        GamePlayGUI gamePlay = new GamePlayGUI(character);

        // Close the loginGUI and display the GamePlayGUI
        this.setVisible(false); // Hide the login GUI
        gamePlay.setVisible(true); // Show the GamePlay GUI
    } else {
        // Handle error: couldn't create character (This can be extended further to provide user feedback)
        System.out.println("Error creating character.");
    }
    }//GEN-LAST:event_jButton1ActionPerformed

